
/**
 * Checks merchant server can negotiate a connection with a TLSv1_2 only endpoint.
 * If not, log warning message
 * 
 * @return {[type]} [description]
 */
var checkTLSv1_2 = exports.checkTLSv1_2 = function checkTLSv1_2(cb) {
    https.get('https://tlstest.paypal.com', function (res) {
        cb(res.statusCode);
    });
};

    it('ensure that check tls works', function (done) {
        util.checkTLSv1_2(function (status) {
            expect(status).to.equal(200);
            done();
        });
    });